mapper.select:
SELECT gm_code
FROM tb_game
WHERE gm_name = #{gm_name}

SELECT mem_nick
FROM tb_mem
WHERE mem_id = #{mem_id}

SELECT ri_seat
FROM tb_recruitinfo
WHERE rcrbrd_num = #{rcrbrd_num}
  AND mem_id = #{mem_id}

SELECT rl_name
FROM tb_role
WHERE rcrbrd_num = #{rcrbrd_num}

SELECT rl_name, rs_seat
FROM tb_roleseat
WHERE rcrbrd_num = #{rcrbrd_num}

SELECT rs_seat
FROM tb_roleseat
WHERE rcrbrd_num = #{rcrbrd_num}
  AND rs_seat = #{rs_seat}

SELECT mem_id, ri_seat, ri_num
FROM tb_recruitinfo
WHERE rcrbrd_num = #{rcrbrd_num}

SELECT mem_id, ri_seat
FROM tb_recruitinfo
WHERE rcrbrd_num = #{rcrbrd_num}

SELECT mm.mem_nick
FROM tb_recruitinfo as ri
         JOIN tb_mem as mm
              ON ri.mem_id = mm.mem_id
WHERE ri.rcrbrd_num = #{rcrbrd_num}
  AND ri.ri_seat = #{ri_seat}

SELECT rcrbrd_max
FROM tb_rcrboard
WHERE rcrbrd_num = #{rcrbrd}

SELECT mm.mem_pic
FROM tb_recruitinfo as ri
         JOIN tb_mem as mm
              ON ri.mem_id = mm.mem_id
WHERE ri.rcrbrd_num = #{rcrbrd_num}
  AND ri.ri_seat = #{ri_seat}

mapper.update:
UPDATE tb_rcrboard
SET rcrbrd_status = '모집완료'
WHERE rcrbrd_num = #{rcrbrd_num}

UPDATE tb_rcrboard
SET rcrbrd_views = rcrbrd_views + 1
WHERE rcrbrd_num = #{rcrbrd_num}

mapper.select:
SELECT mem_id
FROM tb_recruitinfo
WHERE rcrbrd_num = #{rcrbrd_num}
  AND ri_seat = #{ri_seat}

SELECT count(*)
FROM tb_rating
WHERE rcrbrd_num = #{rcrbrd_num}
  AND give_id = #{give_id}
  AND receive_id = #{receive_id}

mapper.delete:
DELETE
FROM tb_rating
WHERE rcrbrd_num = #{rcrbrd_num}
  AND give_id = #{give_id}
  AND receive_id = #{receive_id}

mapper.update:
UPDATE tb_mem
SET good = good + 1
WHERE mem_id = #{mem_id}

UPDATE tb_mem
SET buyer_bad = buyer_bad + 1
WHERE mem_id = #{mem_id}

UPDATE tb_mem
SET upoint = upoint - #{mileage}
WHERE mem_id = #{mem_id}

mapper.select:
SELECT upoint
FROM tb_mem
WHERE mem_id = #{mem_id}

mapper.update:
UPDATE tb_comment
SET com_content = #{com_content}
WHERE com_num = #{com_num}

UPDATE tb_rcrboard
SET rcrbrd_status = '진행중'
WHERE rcrbrd_num = #{rcrbrd_num}
